mapper.update:
UPDATE DayExercise
SET year=#{year}, month=#{month}, exdate=#{exdate},
	exName=#{exName}, exWeight=#{exWeight}, exMeter=#{exMeter}, exTime=#{exTime}, exCount=#{exCount}
	WHERE dayNo=#{dayNo}

mapper.select:

		SELECT *
		  FROM dayExercise
    	  WHERE id=#{loginId} 
		  	AND year=#{condYear} 
		  	AND month=#{condMonth} 
		  	AND exdate=#{condDate}
